import React, {useContext} from 'react';

export const Alert = () => {

    if (!alert) {
        return null
    }

    return (
        <div className={`alert alert-${alert.type || 'warning'} alert-dismissible fade show`}>
            <strong>Holy guacamole!</strong>
            {alert.text}
            <button 
                type="button"
                className="btn-close"
                data-bs-dismiss="alert"
                aria-label="Close">
            </button>
        </div>
    )
}
