import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import {Header} from './components/layout/Header'
import {Form} from './components/employees/Form'
import {Employees} from './components/employees/Employees'
import {Alert} from './components/employees/Alert'
import { AlertState } from './context/alert/AlertState'
import {SignUp} from './pages/SignUp'
import {SignIn}from './pages/SignIn'
import {Home} from './pages/Home'
import Context from './context/context'
import {AlertContext} from './context/alert/alertContext'
import "bootstrap-icons/font/bootstrap-icons.css";
import './css/Custom.css'

function App() {
  const [data, setData] = useState([])

  const updateData = (value) => {
    setData([...data, value])
  }

  const removeEmployee = (email) => {
    setData(data.filter(emp => emp.email !== email))

  }


  return (
    <Context.Provider value={{removeEmployee}}>
      <Router>
        <AlertState>
          <Header/>
          <Alert/>

          <Switch>
            <Route path="/" exact>
              <Form updateData={updateData}/>
              <Employees data={data}/>
            </Route>
            <Route path="/signup" exact>
              <SignUp/>
            </Route>
            <Route path="/login" exact>
              <SignIn/>
            </Route>
          </Switch>
        </AlertState>
      </Router>
    </Context.Provider>
  );
}

export default App;
