import React, {useEffect, useState, useContext, useCallback} from 'react';
import {Link, useHistory} from 'react-router-dom'
import AuthContext from '../../context/AuthContext'


export const Header = (props) => {
    const [isAuthentication, setIsAuthentication] = useState(false)
    const history = useHistory()

    const isAuth = useContext(AuthContext)


    useEffect(() => {
        if (localStorage.getItem('token')){
            setIsAuthentication(true)
            history.push('/')
        }

    }, [isAuth])

    const logoutHandler = () => {

        if(localStorage.getItem('token')) {
            localStorage.removeItem('token')
            history.push('/login')
            setIsAuthentication(false)
        }

        props.updateIsAuthentication(false)



    }

    console.log(isAuthentication, isAuth)

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <div>
                    <Link className="navbar-brand" to="/">Employees</Link>
                </div>
                <div>
                    <button 
                        className="navbar-toggler"
                        type="button"
                        data-bs-toggle="collapse"
                        data-bs-target="#navbar"
                        aria-controls="navbarColor03"
                        aria-expanded="false"
                        aria-label="Toggle navigation"
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                </div>


                <div className="navbar-collapse collapse" style={{ flexGrow: 0 }} id="navbar">
                    <ul className="navbar-nav me-auto">
                        { isAuthentication ? <>
                                <button className="btn">Hello, Username </button>
                                <button 
                                    className="btn btn-info"
                                    onClick={logoutHandler}
                                >
                                    Log out
                                </button>
                            </> : <LoggedOut/>  }
                    </ul>
                </div>
            </div>
        </nav>
    )
}

const LoggedOut = () => {

    return (
        <>
            <li className="nav-item">
                <Link className="nav-link" to="/signup">Sign up</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/login">Sign in</Link>
            </li>
        </>
    )
}
