import React, {useState, useEffect} from 'react';

export const Employees = (data) => {

    const [listData, setListData] = useState([{id: 1, name: "Andrii", email : "and@ssd", message :"Hello, Andrii"}])

    useEffect(data => {
        setListData(listData.push(data))
    }, [data])

    return (
        <div className="col-10 col-sm-10 col-md-10 col-lg-9 mx-auto mt-5">
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Name</th>
                        <th scope="col">Email</th>
                        <th scope="col">Message</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    { listData.map(employeer => 
                        <tr key={employeer.id}>
                            <th scope="row">{employeer.id}</th>
                            <td>{employeer.name}</td>
                            <td>{employeer.email}</td>
                            <td>{employeer.message}</td>
                            <td><button type="button" className="btn btn-sm btn-danger">Delete</button></td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    )
}
