import React, {useState, useContext} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import {Header} from './components/layout/Header'
import {Form} from './components/employees/Form'
import {Employees} from './components/employees/Employees'
import {Alert} from './components/employees/Alert'
import { AlertState } from './context/alert/AlertState'
import {SignUp} from './pages/SignUp'
import {SignIn}from './pages/SignIn'
import {Home} from './pages/Home'
import Context from './context/context'
import AuthContext from './context/AuthContext'
import {AlertContext} from './context/alert/alertContext'
import "bootstrap-icons/font/bootstrap-icons.css";
import './css/Custom.css'

function App() {
  const [isAuthentication, setIsAuthentication] = useState(false)
  const [data, setData] = useState([])

  const updateData = (value) => {
    setData([...data, value])
  }

  const updateIsAuth = (auth) => {
    setIsAuthentication(auth)
  }

  const removeEmployee = (email) => {
    setData(data.filter(emp => emp.email !== email))

  }


  return (
    <AuthContext.Provider value={{isAuthenticationContext}}>
      <Context.Provider value={{removeEmployee}}>
        <Router>
          <AlertState>
            <Header updateIsAuth={updateIsAuth} isAuthentication={isAuthentication}/>
            <Alert/>

            <Switch>
              <Route path="/" exact>
                <Form updateData={updateData}/>
                <Employees data={data}/>
              </Route>
              <Route path="/signup" exact>
                <SignUp/>
              </Route>
              <Route path="/login" exact>
                <SignIn updateIsAuth={updateIsAuth}/>
              </Route>
            </Switch>
          </AlertState>
        </Router>
      </Context.Provider>
    </AuthContext.Provider>
  );
}

export default App;
