import React, {useEffect, useState, useContext} from 'react';
import {Link, useHistory} from 'react-router-dom'
import AuthContext from '../../context/AuthContext'

const storageName = 'token'

export const Header = (props) => {
    const isAuthenticationContext = useContext(AuthContext)
    const [isAuthentication, setIsAuthentication] = useState(isAuthenticationContext)
    const history = useHistory()

    useEffect(() => {
        if (localStorage.getItem('token')){
            setIsAuthentication(true)
        }
    }, [])


    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <div>
                    <Link className="navbar-brand" to="/">Employees</Link>
                </div>
                <div>
                    <button 
                        className="navbar-toggler"
                        type="button"
                        data-bs-toggle="collapse"
                        data-bs-target="#navbar"
                        aria-controls="navbarColor03"
                        aria-expanded="false"
                        aria-label="Toggle navigation"
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                </div>


                <div className="navbar-collapse collapse" style={{ flexGrow: 0 }} id="navbar">
                    <ul className="navbar-nav me-auto">
                        { isAuthentication ? <LoggedIn/> : <LoggedOut/>  }
                    </ul>
                </div>
            </div>
        </nav>
    )
}

const LoggedOut = () => {

    return (
        <>
            <li className="nav-item">
                <Link className="nav-link" to="/signup">Sign up</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/login">Sign in</Link>
            </li>
        </>
    )
}

const LoggedIn = () => {
    const history = useHistory()

    const logoutHandler = async () => {

        if(localStorage.getItem('token')) {
            localStorage.removeItem('token')
            history.push('/login')
        }


    }
    return (
        <>
            <button className="btn">Hello, Username </button>
            <button 
                className="btn btn-warning"
                onClick={logoutHandler}
            >
                Log out
            </button>
        </>
    )
}
