import React, {useEffect, useState} from 'react';
import {Link, useHistory} from 'react-router-dom'
import {LoggedIn} from './LoggedIn'
import {LoggedOut} from './LoggedOut'

const storageName = 'token'

export const Header = () => {
    const [isAuthentication, setIsAuthentication] = useState(false)
    const history = useHistory()

    useEffect(() => {
        if (localStorage.getItem(storageName)) {
            setIsAuthentication(true)
            console.log(isAuthentication)
            history.push('/')

        }
    }, [isAuthentication])

    const logoutHandler = async (e) => {
        e.preventDefault()


        if (true) {

            const res = await fetch(
                'http://localhost:8000/api/auth/logout',
                {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept' : 'application/json'
                    },
                    body: JSON.stringify(form)
                }
            )
            .then(response => {
                if (response.ok)
                    return response.json()
                else {
                    throw new Error(
                        alert.show('Erroe!', 'danger')
                    );
                }
                })


            alert.show('You are logged successfully!', 'success')

            localStorage.setItem(storageName, res.token)


            history.push('/')


        }
        else {
            throw new Error(
                alert.show('Uncorrect data, please enter the valid data!', 'danger')
            )
        }


    }

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <div>
                    <Link className="navbar-brand" to="/">Employees</Link>
                </div>
                <div>
                    <button 
                        className="navbar-toggler"
                        type="button"
                        data-bs-toggle="collapse"
                        data-bs-target="#navbar"
                        aria-controls="navbarColor03"
                        aria-expanded="false"
                        aria-label="Toggle navigation"
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                </div>


                <div className="navbar-collapse collapse" style={{ flexGrow: 0 }} id="navbar">
                    <ul className="navbar-nav me-auto">
                        {isAuthentication ? <LoggedIn/> : <LoggedOut/>  }
                    </ul>
                </div>
            </div>
        </nav>
    )
}
